/**
 * Generate blur data URL for image placeholder
 * This improves LCP by showing a blurred preview while image loads
 */

// Default blur data URL (gray gradient)
export const DEFAULT_BLUR_DATA_URL =
  'data:image/jpeg;base64,'

// Colored blur data URLs for different types
export const BLUR_DATA_URLS = {
  manhwa: 'data:image/jpeg;base64,',
  manga: 'data:image/jpeg;base64,',
  manhua: 'data:image/jpeg;base64,',
}

/**
 * Get blur data URL based on manhwa type
 */
export function getBlurDataUrl(type?: string): string {
  if (!type) return DEFAULT_BLUR_DATA_URL
  
  const normalizedType = type.toLowerCase()
  
  if (normalizedType.includes('manhwa')) return BLUR_DATA_URLS.manhwa
  if (normalizedType.includes('manga')) return BLUR_DATA_URLS.manga
  if (normalizedType.includes('manhua')) return BLUR_DATA_URLS.manhua
  
  return DEFAULT_BLUR_DATA_URL
}

/**
 * Generate shimmer effect for loading state
 */
export function shimmer(w: number, h: number) {
  return `
<svg width="${w}" height="${h}" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
  <defs>
    <linearGradient id="g">
      <stop stop-color="#333" offset="20%" />
      <stop stop-color="#222" offset="50%" />
      <stop stop-color="#333" offset="70%" />
    </linearGradient>
  </defs>
  <rect width="${w}" height="${h}" fill="#333" />
  <rect id="r" width="${w}" height="${h}" fill="url(#g)" />
  <animate xlink:href="#r" attributeName="x" from="-${w}" to="${w}" dur="1s" repeatCount="indefinite"  />
</svg>`
}

/**
 * Convert shimmer SVG to base64 data URL
 */
export function toBase64(str: string) {
  return typeof window === 'undefined'
    ? Buffer.from(str).toString('base64')
    : window.btoa(str)
}

/**
 * Generate shimmer placeholder
 */
export function getShimmerPlaceholder(w: number = 700, h: number = 475) {
  return `data:image/svg+xml;base64,${toBase64(shimmer(w, h))}`
}
